import React, { useState, useCallback, useMemo } from 'react';
import './App.css';
import DemoList from './components/DemoList/DemoList';
import Button from './components/UI/Button/Button';

function App() {
  const [listTitle, setListTitle] = useState('My List');
  const [sortOrder, setSortOrder] = useState('asc'); // Added sortOrder state

  const changeTitleHandler = useCallback(() => {
    if (listTitle === 'My List') {
      setListTitle('New Title');
    } else {
      setListTitle('My List');
    }
  }, [listTitle]);

  const changeOrderHandler = useCallback(() => {
    if (sortOrder === 'asc') {
      setSortOrder('desc');
    } else {
      setSortOrder('asc');
    }
  }, [sortOrder]);

  const listItems = useMemo(() => [5, 3, 1, 10, 9], []);

  const sortedList = useMemo(() => {
    console.log('Items sorted');
    if (sortOrder === 'asc') {
      return listItems.sort((a, b) => a - b);
    } else {
      return listItems.sort((a, b) => b - a);
    }
  }, [listItems, sortOrder]);

  return (
    <div className="app">
      <DemoList title={listTitle} items={sortedList} />
      <Button onClick={changeTitleHandler}>
        {listTitle === 'My List' ? 'Change List Title' : 'Change Back'}
      </Button>
      <Button onClick={changeOrderHandler}>
        {sortOrder === 'asc' ? 'Change to Descending Order' : 'Change to Ascending Order'}
      </Button>
    </div>
  );
}

export default App;
